import { Box } from '@mui/material';
import { DataGrid, GridColDef } from '@mui/x-data-grid';
import moment from 'moment';
import React from 'react';
import { ICandidate } from '../../types/global.typing';
import { baseUrl } from '../../constants/url.constants';

const column: GridColDef[] = [
    {
        field: 'id',
        headerName: 'Id',
        width: 100,
    },
    {
        field: 'firstName',
        headerName: 'First Name',
        width: 200,
    },
    {
        field: 'lastName',
        headerName: 'Last Name',
        width: 200,
    },
    {
        field: 'email',
        headerName: 'Email',
        width: 250,
    },
    {
        field: 'phone',
        headerName: 'Phone Number',
        width: 150,
    },
    {
        field: 'coverLetter',
        headerName: 'Cover Letter',
        width: 200,
    },
    {
        field: 'resumeUrl',
        headerName: 'Download Resume',
        width: 200,
        renderCell: params => (
            <a
                href={`${baseUrl}/Candidate/download/${params.row.resumeUrl}`}
                download
            >
                download CV
            </a>
        ),
    },
    {
        field: 'jobTitle',
        headerName: 'Resume Title',
        width: 300,
    },
    {
        field: 'createdDate',
        headerName: 'Creation time',
        width: 200,
        renderCell: params => moment(params.row.createdDate).fromNow(),
    },
];

interface ICandidatesGridProps {
    data: ICandidate[];
}

const CandidatesGrid = ({ data }: ICandidatesGridProps) => {
    return (
        <Box sx={{ width: '100%', height: 450 }} className='candidates-grid'>
            <DataGrid
                rows={data}
                columns={column}
                getRowId={row => row.id}
                rowHeight={50}
            />
        </Box>
    );
};

export default CandidatesGrid;
